INSERT OR IGNORE INTO LocalizedText
		(Language,	Tag,																Text)
VALUES	('pt_BR',	'LOC_BUILDING_JNR_TARGET_RANGE_NAME',							'Estande de Tiro'),
		('pt_BR',	'LOC_BUILDING_JNR_TARGET_RANGE_DESCRIPTION',					'+25% de experiência de combate para todas as unidades a distância, de reconhecimento e de cerco treinadas nesta cidade.[NEWLINE]Unidades de reconhecimento treinadas nesta cidade começam com uma promoção gratuita.[NEWLINE]Permite treinar Engenheiros Militares nesta cidade.'),
		('pt_BR',	'LOC_BUILDING_BARRACKS_DESCRIPTION_UC_JNR',						'+50% de experiência de combate para todas as unidades corpo-a-corpo e anti-cavalaria treinados nesta cidade.[NEWLINE]Permite treinar Engenheiros Militares nesta cidade.'),
		('pt_BR',	'LOC_BUILDING_STABLE_DESCRIPTION_UC_JNR',						'+25% de experiência de combate para todas as unidades de cavalaria pesada e cavalaria leve treinadas nesta cidade.[NEWLINE]Permite treinar Engenheiros Militares nesta cidade.[NEWLINE]+1 de [ICON_Production] Produção para cada [ICON_RESOURCE_HORSES] Cavalo nesta cidade.'),
		('pt_BR',	'LOC_BUILDING_BASILIKOI_PAIDES_DESCRIPTION_UC_JNR',				'Uma construção exclusiva da Macedônia.[NEWLINE]Ganha [ICON_SCIENCE] Ciência igual a 25% do custo de unidades não civis criadas nesta cidade.[NEWLINE][NEWLINE]+50% de experiência de combate para todas as unidades corpo-a-corpo e anti-cavalaria e +25% de experiência de combate para Heteros treinados nesta cidade.[NEWLINE]Permite treinar Engenheiros Militares nesta cidade.'),
		('pt_BR',	'LOC_BUILDING_ORDU_DESCRIPTION_UC_JNR',							'Uma construção exclusiva da Mongólia.[NEWLINE]Dá uma habilidade que concede +1 de [ICON_MOVEMENT] Movimento para unidades de Cavalarias treinadas nesta cidade.[NEWLINE]Fornece mais pontos de [ICON_GreatGeneral] Grande General.[NEWLINE][NEWLINE]+25% de experiência de combate para todas as unidades de cavalaria pesada e cavalaria leve treinadas nesta cidade.[NEWLINE]Permite treinar Engenheiros Militares nesta cidade.[NEWLINE]+1 de [ICON_Production] Produção para todos [ICON_RESOURCE_HORSES] Cavalos nesta cidade.'),
		('pt_BR',	'LOC_BUILDING_JNR_CAVALIER_NAME',								'Baterias de Artilharia'),
		('pt_BR',	'LOC_BUILDING_JNR_CAVALIER_DESCRIPTION',						'+25% de experiência de combate para todas as unidades de cerco treinadas nesta cidade.[NEWLINE]Unidades de cerco treinadas nesta cidade começam com uma promoção gratuita.[NEWLINE]+2 de [ICON_Strength] Força de Defesa e +5 de [ICON_Ranged] Força de Ataque a Distância por nível de muralha nesta cidade.[NEWLINE]Engenheiros Militares treinados nesta cidade ganham +1 carga.'),
		('pt_BR',	'LOC_BUILDING_JNR_DEPOT_NAME',									'Depósito de Suprimentos'),
		('pt_BR',	'LOC_BUILDING_JNR_DEPOT_DESCRIPTION',							'+25% de experiência de combate para todas as unidades de cavalaria pesada e cavalaria leve treinadas nesta cidade.[NEWLINE]Unidades de Cavalaria Pesada e Cavalaria Leve treinadas nesta cidade começam com uma promoção gratuita.[NEWLINE]+50% de [ICON_Production] Produção para treinar unidades de suporte nesta cidade.[NEWLINE]Permite treinar Médicos e Comboios de Suprimento nesta cidade.'),
		('pt_BR',	'LOC_BUILDING_ARMORY_DESCRIPTION_UC_JNR',						'+50% de [ICON_Production] de Produção para todas unidades de corpo-a-corpo, anti-cavalaria, reconhecimento e a distância nesta cidade.'),
		('pt_BR',	'LOC_BUILDING_JNR_ARSENAL_NAME',								'Paiol'),
		('pt_BR',	'LOC_BUILDING_JNR_ARSENAL_DESCRIPTION',							'+50% de [ICON_Production] Produção para treinar todas as unidades de Cerco, Cavalaria Pesada e Cavalaria Leve nesta cidade.[NEWLINE]Permite Corporações e Exércitos serem treinados diretamente. O custo de treinar Corporações e Exércitos é reduzido em 25%.'),
		('pt_BR',	'LOC_BUILDING_JNR_PRISON_NAME',									'Prisão'),
		('pt_BR',	'LOC_BUILDING_JNR_PRISON_DESCRIPTION',							'Este Acampamento ganha um bônus adicional de adjacência (+1 de [ICON_Production] Produção) de pedreiras.[NEWLINE]Fornece +4 de Lealdade por turno para esta cidade.[NEWLINE]Espiões inimigos operam como 2 níveis abaixo do normal nesta cidade.[NEWLINE]Quando descontente, esta cidade recebe +15% de [ICON_Production] Produção. Quando Infeliz, o bônus é dobrado. Em desordem civil, o bônus é triplicado. Cuidado: durante uma revolta, o bônus é perdido.'),
		('pt_BR',	'LOC_BUILDING_MILITARY_ACADEMY_DESCRIPTION_UC_JNR',				'+25% de experiência de combate para todas as unidades de corpo-a-corpo, anti-cavalaria, reconhecimento e a distância treinadas nesta cidade.[NEWLINE]Unidades de corpo-a-corpo, anti-cavalaria e a distância treinadas nesta cidade começam com uma promoção gratuita. Unidades de reconhecimento treinadas nesta cidade começam com a promoção Emboscada.[NEWLINE]Permite Corporações e Exércitos serem treinados diretamente. O custo de treinar Corporações e Exércitos é reduzido em 25%.[NEWLINE]Fornece mais pontos de [ICON_GreatGeneral] Grande General.'),
		('pt_BR',	'LOC_BUILDING_SHIPYARD_DESCRIPTION_UC_JNR',						'+50% de [ICON_Production] Produção para todas as unidades navais nesta cidade.[NEWLINE]Bônus de Produção igual ao bônus de adjacência do distrito do Porto.[NEWLINE]+1 de [ICON_Production] Produção para todos os painéis litorâneos não melhorados desta cidade.'),
		('pt_BR',	'LOC_BUILDING_JNR_NAVAL_BASE_NAME',								'Base Naval'),
		('pt_BR',	'LOC_BUILDING_JNR_NAVAL_BASE_DESCRIPTION',						'+25% de experiência de combate para todas as unidades navais treinadas nesta cidade.[NEWLINE]Todas as unidades navais treinadas nesta cidade começam com uma promoção gratuita.[NEWLINE]Permite Frotas e Armadas serem treinadas diretamente. O custo de treinar Frotas e Armadas é reduzido em 25%.[NEWLINE]Fornece mais pontos de [ICON_GreatAdmiral] Grande Almirante.'),
		('pt_BR',	'LOC_BUILDING_SEAPORT_DESCRIPTION_UC_JNR_MIL',					'+2 de [ICON_GOLD] Ouro para todos os painéis litorâneos desta cidade.'),
		('pt_BR',	'LOC_BUILDING_HANGAR_DESCRIPTION_UC_JNR_MIL',					'+50% de [ICON_Production] Produção para todas as unidades aéreas treinadas nesta cidade.[NEWLINE]+1 espaço para unidades aéreas no Aeródromo.'),
		('pt_BR',	'LOC_BUILDING_AIRPORT_NAME_UC_JNR',								'Base de Força Aérea'),
		('pt_BR',	'LOC_BUILDING_AIRPORT_DESCRIPTION_UC_JNR_MIL',					'+50% de experiência de combate para todas as unidades aéreas treinadas nesta cidade.[NEWLINE]Unidades Aéreas treinadas nesta cidade começam com uma promoção gratuita.[NEWLINE]+2 espaços para unidades aéreas no Aeródromo.[NEWLINE]Permite a capacidade de transporte aéreo de unidades terrestres entre distritos de Aeródromos com Aeroportos depois que o cívico mobilização rápida for desbloqueado.'),
		('pt_BR',	'LOC_ABILITY_JNR_TARGET_RANGE_TRAINED_UNIT_XP_DESCRIPTION',		'+25% de experiência recebida de Estande de Tiro'),
		('pt_BR',	'LOC_ABILITY_JNR_CAVALIER_TRAINED_UNIT_XP_DESCRIPTION',			'+25% de experiência recebida de Baterias de Artilharia'),
		('pt_BR',	'LOC_ABILITY_JNR_DEPOT_TRAINED_UNIT_XP_DESCRIPTION',			'+25% de experiência recebida de Depósito de Suprimentos'),
		('pt_BR',	'LOC_ABILITY_BARRACKS_TRAINED_UNIT_XP_DESCRIPTION_JNR_UC',		'+50% de experiência recebida de Caserna'),
		('pt_BR',	'LOC_ABILITY_SEAPORT_TRAINED_UNIT_XP_DESCRIPTION_JNR_UC',		'+25% de experiência recebida de Base Naval'),
		('pt_BR',	'LOC_UNIT_MILITARY_ENGINEER_DESCRIPTION_JNR_UC',				'Unidade de suporte da era Medieval. Requer uma Caserna, Estábulo ou Estande de Tiro para produzir. Pode construir Estradas, Ferrovias, Fortes, Pistas de Pouso, Silos de Mísseis e Túneis de Montanha. Uma de suas cargas de produção pode ser usada para completar 20% de um Canal, Represa ou Aqueduto ou na construção de uma barreira contra enchentes'),
		('pt_BR',	'LOC_UNIT_MEDIC_DESCRIPTION_JNR_UC',							'Unidade de suporte da era Industrial. Requer um Depósito de Suprimentos para produzir. Pode curar unidades adjacenntes.'),
		('pt_BR',	'LOC_UNIT_SUPPLY_CONVOY_DESCRIPTION_JNR_UC',					'Unidade de suporte da era Moderna. Requer um Depósito de Suprimentos para produzir. Aumenta a cura para unidades adjacentes. Concede às unidades bônus de +1 de [ICON_Movement] Movimento se o movimento começar adjacente ao Comboio.'),
		('pt_BR',	'LOC_POLICY_LIMES_DESCRIPTION_JNR_UC',							'+100% de bônus de adjacência de distrito de acampamento.[NEWLINE]+100% de [ICON_Production] Produção para edificações defensivas.'),
		('pt_BR',	'LOC_POLICY_THIRD_ALTERNATIVE_DESCRIPTION_JNR_UC_MIL',			'+100% de bônus de adjacência de distrito de acampamento.[NEWLINE]+2 de [ICON_Culture] Cultura e +4 de [ICON_Gold] Ouro de cada Laboratório de Pesquisa, Academia Militar, Paiol e Prisão.'),
		('pt_BR',	'LOC_POLICY_MILITARY_RESEARCH_DESCRIPTION_JNR_UC',				'Academias Militares, Paióis, Prisões, Bases Navais, Hangares e Muralhas da Renascença geram +2 de [ICON_Science] Science.'),
		('pt_BR',	'LOC_POLICY_INTEGRATED_SPACE_CELL_DESCRIPTION_JNR_UC',			'+15% de [ICON_Production] Produção para projetos da Corrida Espacial se a cidade possuir uma Academia Militar, Paiol, Prisão, Base Naval ou Hangar.'),
		('pt_BR',	'LOC_BOOST_TRIGGER_GUNPOWDER_JNR_UC',							'Construa um Arsenal ou uma Baterias de Artilharia.'),
		('pt_BR',	'LOC_BOOST_TRIGGER_LONGDESC_GUNPOWDER_JNR_UC',					'Seus armeiros estão desenvolvendo novas armas que irão devastar seus oponentes.'),
		('pt_BR',	'LOC_BOOST_TRIGGER_TOTALITARIANISM_JNR_UC',						'Construa 2 Prisões.'),
		('pt_BR',	'LOC_BOOST_TRIGGER_LONGDESC_TOTALITARIANISM_JNR_UC',			'A obediência instilada pelas suas prisões é agora uma segunda natureza para seus cidadões.');
		
